import React from 'react';

const GameCard = ({ image, title, price, score }) => {
    return (
        <div className='bg-[#EAB2CE] mx-auto mt-[2rem] rounded-lg  max-w-2xl'>    
            <div className="h-[17rem] w-full flex">
                <div className='w-[60%] h-full flex'>
                    <img 
                        src={image} 
                        alt={title} 
                        className="object-cover w-full rounded-lg" 
                    />
                </div>
                <div className="w-[40%] h-full flex flex-col justify-center items-center space-y-6">
                    <h2 className="text-lg font-bold text-[25px]">
                        {title}
                    </h2>
                    <p className="text-gray-700 text-sm ">
                        Rating: {score}
                    </p>
                    <div className="flex justify-between items-center">
                        <span className="">
                            ${price}
                        </span>
                    </div>
                    <button className="bg-[#994B54] rounded mt-2">
                        <p className='text-black my-1 mx-1'>
                            View Details
                        </p>
                    </button>
                </div>
            </div>
        </div>
    );
};

export default GameCard;
