import React from 'react';

const GameCard = ({ image, title, price, description }) => {
    return (
        <div className="bg-[#EAB2CE] rounded-lg  max-w-2xl justify-center">
            <img 
                src={image} 
                alt={title} 
                className="h-40 object-cover" 
            />
            <div className="p-4">
                <h2 className="text-lg font-bold mb-2">
                    {title}
                </h2>
                <p className="text-gray-700 text-sm mb-4">
                    {description}
                </p>
                <div className="flex justify-between items-center">
                    <span className="text-lg font-bold">
                        ${price}
                    </span>
                    <button className="bg-[#994B54] text-black px-4 py-2 rounded">
                        View Details
                    </button>
                </div>
            </div>
        </div>
    );
};

export default GameCard;
